import React, { useState } from 'react';
import Swal from 'sweetalert2';

export default function Host() {
  const url = 'http://127.0.0.1:8000/posts/';
  const fileUrl = 'http://127.0.0.1:8000/posts/savefile';

  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setFileName(e.target.files[0].name);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      try {
        const fileResponse = await fetch(fileUrl, {
          method: 'POST',
          body: formData,
        });

        if (fileResponse.ok) {  

          console.log(fileName);

          const data = new FormData(e.target);
        



          try {
            const response = await fetch(url, {
              method: 'POST',
              body: JSON.stringify(data)
            });

            if (response.ok) {
              const data = await response.json();
              if (data === 'Added Successfully') {
                Swal.fire({
                  title: 'Post Added Successfully!',
                  color: 'var(--color-5)',
                  background: 'var(--color-2)',
                  icon: 'success',
                  confirmButtonColor: 'var(--color-5)',
                  confirmButtonText: 'Okay!',
                  iconColor: 'var(--color-5)',
                }).then(() => {
                  window.location = '/dashboard';
                });
              }
            }
          } catch (error) {
            console.log(error);
          }
        }
      } catch (error) {
        console.log(error);
      }
    }
  };

  return (
    <>
      <h1 className="ml-4 mt-4 font-weight-bolder " style={{ fontSize: '40px' }}>
        Host now
      </h1>
      <hr
        style={{
          backgroundColor: 'var(--color-5)',
          height: '2px',
          border: 'none',
          borderRadius: '10px',
          margin: '0',
          marginBottom: '15px',
        }}
      />
      <div className="container">
        <form onSubmit={handleSubmit}>
          <div
            className="d-flex flex-column"
            style={{
              overflow: 'hidden',
              backgroundColor: 'var(--color-2)',
              padding: '10px',
              borderRadius: '7px',
            }}
          >
            <select name="type" className="type-select" required>
              <option disabled>---Select type of Post---</option>
              <option value="Professional">Professional</option>
              <option value="Funny">Funny</option>
            </select>
            <textarea
              name="title"
              className="w-100"
              id=""
              cols="30"
              rows="10"
              style={{
                backgroundColor: 'var(--color-2)',
                color: 'var(--color-5)',
                border: 'none',
                resize: 'none',
                padding: '15px',
              }}
              required
            ></textarea>
            <input type="file" onChange={handleFileChange} />
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    </>
  );
}
