import React from 'react'
import alertify from 'alertifyjs';

export default function Host() {

  const url = 'http://127.0.0.1:8000/posts/';

  const form = document.querySelector('form');

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = new FormData(e.target);
    fetch(url, {
      method: 'POST',
      body: JSON.stringify({
        user_id : 1, 
        title: data.get('title'),
        likes_count: 0,
        share_count: 0,
        type: 'Professional',
      }),
    })
    .then(response => response.json())
    .then(data => {
      if (data === "Added Successfully") {
        alertify.success('Post created successfully', { 
          position: 'top-right',
          background: 'var(--color-4)',
          color: 'var(--color-1)',
          padding: '10px',
          borderRadius: '5px',
          fontSize: '20px',
        });

        if (form) {
          form.reset();
        }
      }

    })
    .catch(error => console.log(error));
  }

  return (
    <>
      <h1 className='ml-4 mt-4 font-weight-bolder ' style={{ fontSize: "45px" }}>Host now</h1>
      <div className="container">
      <form action="" onSubmit={handleSubmit}>
        <div className="i" style={{overflow:'hidden'}}>
          <textarea name="title" className='form-control' id="" cols="30" rows="10" style={{backgroundColor:'var(--color-2)',color:'var(--color-5)',border:'none'}}></textarea>
          <button className="btn btn-primary" type="submit">Send</button>
        </div>
      </form>
      </div>
    </>
  );

}
